	function dup_check ($a_result) {
		$address = addslashes($a_result[1]);
		$city = addslashes($a_result[2]);
		$state = $a_result[3];
		$zip = $a_result[4];
		
		$query = "SELECT leads_id FROM leads WHERE address1='$address' AND city='$city' AND state='$state' AND zip='$zip'";
		$result = mysql_query($query) or die("Query failed : " . mysql_error());
		while ($line = mysql_fetch_array($result, MYSQL_ASSOC)) {
			$leads_id = $line["leads_id"];
		}
		mysql_free_result($result);
		
		if($leads_id != "") {
			$ret_dup = -1;
			//echo "Store matches in leads table\n";
		} else {
			$query = "SELECT retailer_id FROM retailer WHERE address1='$address' AND city='$city' AND state='$state' AND zip='$zip'";
			$result = mysql_query($query) or die("Query failed : " . mysql_error());
			while ($line = mysql_fetch_array($result, MYSQL_ASSOC)) {
				$retailer_id = $line["retailer_id"];
			}
			mysql_free_result($result);
			
			if($retailer_id != "") {
				$ret_dup = -2;
				//echo "Store matches in retailer table\n";
			} else {
				$ret_dup = 1;
			}
		}
		
		return $ret_dup;

	} // end function
	
